package com.syntax.class09;

public class PatternNestedDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int r=7;r>=1;r--) {
			for(int c=1;c<=6;c++) {
				System.out.print(r);
			}
			System.out.println();
		}
		System.out.println();
		for(int a=1;a<=5;a++) {
			for(int b=7;b>=1;b--) {
				System.out.print(b);
			}
			System.out.println();
		}

	}

}
